#include<iostream>using namespace std;int main(){	int frascos, pildoras, betamol, micilina, sinitico;		cout << "Cantidad de Frascos:";	cin >> frascos;			// hacemos los calculos		pildoras = frascos * 75;	betamol = pildoras * 45;	micilina = pildoras * 2000;	sinitico = pildoras * 7;			cout << "Pildoras: ";	cout << pildoras;	cout << endl;		cout << "Betamol: ";	cout << betamol;	cout << endl;		cout << "Micilina: ";	cout << micilina;	cout << endl;		cout << "Sinitico: ";	cout << sinitico;	cout << endl;						}
